column46:
@charset "UTF-8";
@import url("https://fonts.googleapis.com/css2?family=Lato:wght@300;700;900&display=swap");
:root {
  --gray-empeor: #555555;
  --gray-mine-shaft: #232323;
  --gray-dove: #707070;
  --gray-alto: #DEDEDE;
  --gray-ship: #414042;
  --orange-web: #FFA400;
}

.-translate-x-1\/2 {
  transform: translateX(-50%);
}

.-translate-x-y-1\/2 {
  transform: translate(-50%, -50%);
}

@media screen and (min-width: 768px) {
  .md\:-translate-x-1\/2 {
    transform: translateX(-50%);
  }
}

@media screen and (min-width: 768px) {
  .md\:-translate-x-0 {
    transform: translateX(0);
  }
}

@media screen and (min-width: 1280px) {
  .lg\:-translate-x-1\/2 {
    transform: translateX(-50%);
  }
}

@media screen and (min-width: 1280px) {
  .lg\:-translate-x-0 {
    transform: translateX(0);
  }
}

.-translate-y-1\/2 {
  transform: translateY(-50%);
}

@media screen and (min-width: 768px) {
  .md\:-translate-y-1\/2 {
    transform: translateY(-50%);
  }
}

@media screen and (min-width: 768px) {
  .md\:-translate-y-0 {
    transform: translateY(0);
  }
}

@media screen and (min-width: 1280px) {
  .lg\:-translate-y-1\/2 {
    transform: translateY(-50%);
  }
}

@media screen and (min-width: 1280px) {
  .lg\:-translate-y-0 {
    transform: translateY(0);
  }
}

.mobile-only {
  display: none;
}

@media only screen and (max-width: 1023px) {
  .mobile-only {
    display: block;
  }
  .desktop-only {
    display: none !important;
  }
}
.default ol {
  list-style: decimal;
}

.default ul {
  list-style: disc;
}

.font-bold {
  font-weight: bold !important;
}

.uppercase {
  text-transform: uppercase !important;
}

.text-white, .text-white * {
  color: white !important;
}

* {
  font-family: Lato, sans-serif !important;
}

.component-cart-count-default, .main-header .component-cart-count, .top-header .component-cart-count {
  position: relative;
}
.component-cart-count-default .cart-count-badge, .main-header .component-cart-count .cart-count-badge, .top-header .component-cart-count .cart-count-badge {
  position: absolute;
  left: -19px;
  top: -16px;
  background: var(--orange-web);
  width: 15px;
  height: 15px;
  border-radius: 50%;
  display: flex;
  justify-content: center;
  align-items: center;
}
.component-cart-count-default .cart-count-badge .amount, .main-header .component-cart-count .cart-count-badge .amount, .top-header .component-cart-count .cart-count-badge .amount {
  font-size: 10px;
  color: black !important;
  position: absolute;
  top: -2px;
}
.component-cart-count-default .cart-count-badge .item-text, .main-header .component-cart-count .cart-count-badge .item-text, .top-header .component-cart-count .cart-count-badge .item-text {
  display: none;
}

@media screen and (min-width: 1280px) {
  #nogin-element-header-super-promo, #nogin-element-super-promo-mobile {
    width: 33%;
  }
}
#nogin-element-header-super-promo .slick-slider, #nogin-element-super-promo-mobile .slick-slider {
  min-height: auto !important;
}
#nogin-element-header-super-promo .slick-slider .slick-next::before, #nogin-element-header-super-promo .slick-slider .slick-prev::before, #nogin-element-super-promo-mobile .slick-slider .slick-next::before, #nogin-element-super-promo-mobile .slick-slider .slick-prev::before {
  color: rgb(253, 162, 21) !important;
}
#nogin-element-header-super-promo .slick-slider .slick-next::before, #nogin-element-super-promo-mobile .slick-slider .slick-next::before {
  position: unset !important;
}

.basic-search-form form, .main-header .lum--block--search-input form,
.main-header .side-menu form {
  outline: none;
  display: flex;
  position: relative;
}
.basic-search-form form input, .main-header .lum--block--search-input form input,
.main-header .side-menu form input {
  padding: 10px;
  height: 43px;
  line-height: 43px;
  font-size: 15px;
  font-weight: 300;
  font-style: italic;
  color: var(--gray-mine-shaft) !important;
  background-size: 20px;
